import React, { useState } from 'react';
import { Dialog, DialogTitle, DialogContent, TextField, IconButton, Box, List, ListItem, ListItemText, Paper, Avatar } from '@mui/material';
import SendIcon from '@mui/icons-material/Send';
import CloseIcon from '@mui/icons-material/Close';
import { styled } from '@mui/system';

const MessageList = styled(List)({
  overflowY: 'auto',
  maxHeight: '300px',
  padding: '10px',
});

const MessageInputContainer = styled(Box)({
  display: 'flex',
  padding: '10px',
  borderTop: '1px solid #ccc',
});

const Message = styled(Paper)(({ isSender }) => ({
  padding: '10px',
  margin: '10px 0',
  backgroundColor: isSender ? '#e1f5fe' : '#f1f8e9',
  alignSelf: isSender ? 'flex-end' : 'flex-start',
  maxWidth: '70%',
  display: 'flex',
  alignItems: 'center',
}));

const MessageAvatar = styled(Avatar)({
  marginRight: '10px',
});

export default function ChatDialog({ open, onClose }) {
  const [messages, setMessages] = useState([
    { text: 'Hi there!', sender: 'other' },
    { text: 'Hello! How are you?', sender: 'self' },
    { text: 'I am good, thank you! How about you?', sender: 'other' },
  ]);
  const [newMessage, setNewMessage] = useState('');

  const handleSendMessage = () => {
    if (newMessage.trim() !== '') {
      setMessages([...messages, { text: newMessage, sender: 'self' }]);
      setNewMessage('');
    }
  };

  return (
    <Dialog open={open} onClose={onClose} fullWidth maxWidth="sm">
      <DialogTitle>
        Chat
        <IconButton
          aria-label="close"
          onClick={onClose}
          sx={{
            position: 'absolute',
            right: 8,
            top: 8,
            color: (theme) => theme.palette.grey[500],
          }}
        >
          <CloseIcon />
        </IconButton>
      </DialogTitle>
      <DialogContent dividers>
        <MessageList>
          {messages.map((message, index) => (
            <ListItem key={index} disableGutters>
              <Message isSender={message.sender === 'self'}>
                {message.sender === 'other' && <MessageAvatar>A</MessageAvatar>}
                <ListItemText primary={message.text} />
              </Message>
            </ListItem>
          ))}
        </MessageList>
        <MessageInputContainer>
          <TextField
            fullWidth
            variant="outlined"
            placeholder="Type a message..."
            value={newMessage}
            onChange={(e) => setNewMessage(e.target.value)}
            onKeyPress={(e) => {
              if (e.key === 'Enter') {
                handleSendMessage();
              }
            }}
          />
          <IconButton color="primary" onClick={handleSendMessage}>
            <SendIcon />
          </IconButton>
        </MessageInputContainer>
      </DialogContent>
    </Dialog>
  );
}
