import React, { useState } from 'react';
import {
  AppBar,
  Toolbar,
  Typography,
  Box,
  List,
  ListItem,
  ListItemAvatar,
  ListItemText,
  Avatar,
  Badge,
  IconButton,
  Paper,
  Button,
  Dialog,
  DialogActions,
  DialogContent,
  DialogContentText,
  DialogTitle,
  TextField
} from '@mui/material';
import NotificationsIcon from '@mui/icons-material/Notifications';
import CloseIcon from '@mui/icons-material/Close';
import DeleteIcon from '@mui/icons-material/Delete';
import MarkEmailReadIcon from '@mui/icons-material/MarkEmailRead';
import EmailIcon from '@mui/icons-material/Email';
import { styled } from '@mui/system';

const NotificationsContainer = styled(Paper)(({ theme }) => ({
  position: 'fixed',
  top: 50,
  right: 20,
  width: '50%', // Make it take up half the screen width
  height: '50vh', // Make it take up half the screen height
  borderRadius: 10,
  overflow: 'hidden',
  boxShadow: theme.shadows[3],
  backgroundColor: '#f5f5f5', // Light brownish background
  color: '#3e2723', // Deep brown color
}));

const NotificationHeader = styled(Box)(({ theme }) => ({
  display: 'flex',
  justifyContent: 'space-between',
  alignItems: 'center',
  padding: theme.spacing(1),
  backgroundColor: '#6d4c41', // Medium brown background
  color: theme.palette.common.white,
}));

const NotificationList = styled(List)(({ theme }) => ({
  maxHeight: '80%', // Adjust height to fit container
  overflowY: 'auto',
}));

const NotificationItem = styled(ListItem)(({ theme }) => ({
  '&:hover': {
    backgroundColor: theme.palette.action.hover,
  },
  display: 'flex',
  justifyContent: 'space-between',
  alignItems: 'center'
}));

const notificationsData = [
  { id: 1, title: 'Template Published', time: '3d', read: false },
  { id: 2, title: 'Template Published', time: '1mo', read: true },
  { id: 3, title: 'Template Published', time: '1mo', read: true },
  { id: 4, title: 'Template Published', time: '1mo', read: true },
  { id: 5, title: 'Template Published', time: '1mo', read: true },
  { id: 6, title: 'Template Published', time: '1mo', read: true },
  { id: 7, title: 'Template Published', time: '1mo', read: true },
];

export default function Inbox() {
  const [open, setOpen] = React.useState(true);
  const [notifications, setNotifications] = useState(notificationsData);
  const [mailDialogOpen, setMailDialogOpen] = useState(false);
  const [selectedNotification, setSelectedNotification] = useState(null);

  const handleClose = () => {
    setOpen(false);
  };

  const handleDelete = (id) => {
    setNotifications(notifications.filter(notification => notification.id !== id));
  };

  const handleRead = (id) => {
    setNotifications(notifications.map(notification =>
      notification.id === id ? { ...notification, read: true } : notification
    ));
  };

  const handleRespond = (notification) => {
    setSelectedNotification(notification);
    setMailDialogOpen(true);
  };

  const handleMailDialogClose = () => {
    setMailDialogOpen(false);
    setSelectedNotification(null);
  };

  return (
    <NotificationsContainer>
      <NotificationHeader>
        <Typography variant="h6">Notifications</Typography>
        <Badge badgeContent={notifications.filter(n => !n.read).length} color="secondary">
          <NotificationsIcon />
        </Badge>
        <IconButton color="inherit" size="small" onClick={handleClose}>
          <CloseIcon />
        </IconButton>
      </NotificationHeader>
      <NotificationList>
        {notifications.map((notification) => (
          <NotificationItem key={notification.id} button>
            <ListItemAvatar>
              <Avatar style={{ backgroundColor: notification.read ? '#ccc' : '#6d4c41' }}>
                <NotificationsIcon />
              </Avatar>
            </ListItemAvatar>
            <ListItemText
              primary={notification.title}
              secondary={notification.time}
            />
            <Box>
              <IconButton onClick={() => handleDelete(notification.id)}>
                <DeleteIcon />
              </IconButton>
              <IconButton onClick={() => handleRead(notification.id)}>
                <MarkEmailReadIcon />
              </IconButton>
              <IconButton onClick={() => handleRespond(notification)}>
                <EmailIcon />
              </IconButton>
            </Box>
          </NotificationItem>
        ))}
      </NotificationList>
      <Box p={1} textAlign="center">
        <Typography variant="caption" color="textSecondary">
          Powered by Courier
        </Typography>
      </Box>
      <Dialog open={mailDialogOpen} onClose={handleMailDialogClose}>
        <DialogTitle>Send Response</DialogTitle>
        <DialogContent>
          <DialogContentText>
            Responding to: {selectedNotification?.title}
          </DialogContentText>
          <TextField
            autoFocus
            margin="dense"
            label="Your Message"
            type="text"
            fullWidth
            variant="outlined"
          />
        </DialogContent>
        <DialogActions>
          <Button onClick={handleMailDialogClose} color="primary">
            Cancel
          </Button>
          <Button onClick={handleMailDialogClose} color="primary">
            Send
          </Button>
        </DialogActions>
      </Dialog>
    </NotificationsContainer>
  );
}
